import React, { Component } from "react";
import logo from "./logo.svg";
import "./Ziget.css";

export default class Ziget extends Component {
  constructor(props) {
    super(props);
    this.state = {
      usernames: "",
      individual: false
    };

    this.handleTAChange = this.handleTAChange.bind(this);
    this.handleCBChange = this.handleCBChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
  }

  handleTAChange = event => this.setState({ usernames: event.target.value });
  handleCBChange = event => this.setState({ individual: event.target.checked });

  handleSubmit = () => {
    const url = "https://fe-test-zyper-engagement.herokuapp.com/start";
    const inputData = {
      username: this.state.usernames,
      individual: this.state.individual
    };
    console.log(JSON.stringify(inputData));
    fetch(url, {
      method: "POST",
      mode: "cors",
      cache: "no-cache",
      body: JSON.stringify(inputData),
      headers: {
        "Content-Type": "application/json",
        Accept: "*/*",
        "Cache-Control": "no-cache",
        Host: "fe-test-zyper-engagement.herokuapp.com",
        Connection: "keep-alive"
      }
    }).then(jobID => console.log(`JOB ID`, jobID.json()));
  };

  render() {
    return (
      <div className="Ziget">
        <header className="Ziget-header">
          <img src={logo} className="Ziget-logo" alt="logo" />
        </header>
        <form className="Ziget-form">
          <label className="textarea-label" htmlFor="usernames">
            Username(s):
            <textarea
              id="usernames"
              value={this.state.value}
              onChange={this.handleTAChange}
              autoFocus
              required
              rows={10}
              cols={100}
              placeholder="Enter comma(,) seperated Instagram handle. Example: abc,abc_def,abc.def"
            />
          </label>
          <p className="instructions">
            Instructions: For the sake of this example please enter{" "}
            <b>comma(,)</b>&nbsp;seperated Instagram handles only.
            <br />
            <b>Example: abc,abc_def,abc.def</b>. <br />
            Please don't use spaces.
          </p>
          <label className="checkbox-label">
            <input type="checkbox" onChange={this.handleCBChange} />
            Tick to list all accounts individually.
          </label>
          <button type="button" className="submit" onClick={this.handleSubmit}>
            Submit
          </button>
          <button type="reset" className="reset">
            Clear
          </button>
        </form>
      </div>
    );
  }
}
