import React, { Component } from "react";
import logo from "./logo.svg";
import "./Ziget.css";

export default class Ziget extends Component {
  constructor(props) {
    super(props);
    this.state = {
      usernames: "",
      individual: false,
      jobId: "",
      data: {},
      loading: false
    };

    this.handleTAChange = this.handleTAChange.bind(this);
    this.handleCBChange = this.handleCBChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
  }

  handleTAChange = event => this.setState({ usernames: event.target.value });
  handleCBChange = event => this.setState({ individual: event.target.checked });

  handleSubmit = () => {
    this.setState({ loading: true });
    const url = "https://fe-test-zyper-engagement.herokuapp.com/start";
    const inputData = {
      username: this.state.usernames,
      individual: this.state.individual
    };
    console.log(JSON.stringify(inputData));
    fetch(url, {
      method: "POST",
      body: JSON.stringify(inputData),
      headers: {
        "Content-Type": "application/json"
      }
    })
      .then(response => response.text())
      .then(jobId => {
        console.log(jobId);
        const url =
          "https://fe-test-zyper-engagement.herokuapp.com/results/" + jobId;
        fetch(url, {
          headers: {
            Connection: "keep-alive"
          }
        }).then(r => {
          if (r.ok) {
            console.log(r);
          }
        });
      });
  };

  getJobID = () => {
    const url = "https://fe-test-zyper-engagement.herokuapp.com/start";
    const inputData = {
      username: this.state.usernames,
      individual: this.state.individual
    };
    console.log(JSON.stringify(inputData));
    fetch(url, {
      method: "POST",
      body: JSON.stringify(inputData),
      headers: {
        "Content-Type": "application/json"
      }
    })
      .then(response => response.text())
      .then(jobId => this.setState({ jobId }));
  };

  getData = () => {
    const urlWithJobID =
      "https://fe-test-zyper-engagement.herokuapp.com/results/" +
      this.state.jobId;
    let timeout = "";
    let c = 0;
    const fetcher = () => {
      fetch(urlWithJobID)
        .then(response => {
          response.json();
          console.log(response.json())
          if (response.status === 202) {
            console.log(response);
          }
          if (response.status === 200) {
            response.json();
            console.log(`HIT`);
            console.log(response);
            this.setState({ loading: false });
            clearInterval(timeout);
          }
          console.log(c);
          timeout = setInterval(fetcher, 2000);
          c++;
        })
        .then(data => {
          console.log(`Data: `, data);
          this.setState({ data });
        });
    };
    fetcher();
  };

  render() {
    return (
      <div className="Ziget">
        <header className="Ziget-header">
          <img src={logo} className="Ziget-logo" alt="Zyper Logo" />
        </header>
        <form id="ziget-form" className="Ziget-form">
          <label className="textarea-label" htmlFor="usernames">
            Username(s):
            <textarea
              id="usernames"
              value={this.state.value}
              onChange={this.handleTAChange}
              autoFocus
              required
              rows={10}
              cols={100}
              placeholder="Enter comma(,) seperated Instagram handle. Example: abc,abc_def,abc.def"
            />
          </label>
          <p className="instructions">
            Instructions: For the sake of this example please enter{" "}
            <b>comma(,)</b>&nbsp;seperated Instagram handles only.
            <br />
            <b>Example: abc,abc_def,abc.def</b>. <br />
            Please don't use spaces.
          </p>
          <label className="checkbox-label">
            <input
              type="checkbox"
              id="individual"
              onChange={this.handleCBChange}
            />
            Tick to list all accounts individually.
          </label>
          <button type="button" className="submit" onClick={this.handleSubmit}>
            Submit
          </button>
          <button type="reset" className="reset">
            Clear
          </button>
        </form>
        <h3>Results:</h3>
        <div className="loader" hidden={!this.state.loading} />
        <div className="output" hidden={this.state.loading} />
      </div>
    );
  }
}
